import { Close, Send } from '@mui/icons-material';
import { 
  Button,
  Dialog, 
  DialogActions, 
  DialogContent, 
  DialogContentText, 
  DialogTitle, 
  IconButton, 
  TextField 
} from '@mui/material'
import React, { useEffect, useRef, useState } from 'react'
import { useValue } from '../context/ContextProvider'
import GoogleOneTapLogin from './GoogleOneTapLogin';
import PasswordField from './PasswordField';

const Login = () => {
  const {
    state: { openLogin },
    dispatch,
  } = useValue();
  const [title, setTitle] = useState("Login");
  const [isRegistr, setRegistr] = useState(false);
  const nameRef = useRef();
  const secondNameRef = useRef();
  const emailRef = useRef();
  const passwordRef = useRef();
  const confirmPasswordRef = useRef();

  const handleClose = () => {
    dispatch({ type: "CLOSE_LOGIN" });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // testing loading
    dispatch({ type: "START_LOADING" });

    setTimeout(() => {
      dispatch({ type: "END_LOADING" });
    }, 6000);

    //testing Notification
    const password = passwordRef.current.value;
    const confirmPassword = confirmPasswordRef.current.value;
    if (password !== confirmPassword) {
      dispatch({
        type: "UPDATE_ALERT",
        payload: {
          open: true,
          severity: "error",
          message: "Passwords do not match",
        },
      });
    }
  };
// заголовки
  useEffect(() => {
    isRegistr ? setTitle("Реєстрація") : setTitle("Вхід на сайт");
  }, [isRegistr]);

  return (
    <Dialog open={openLogin} onClose={handleClose}>
      <DialogTitle>
        {title}
        <IconButton
        sx={{
          position: 'absolute',
          top: 8,
          right: 8,
          color: (theme) => theme.palette.grey[500],
        }}
        onClick={handleClose}
        >
          <Close />
        </IconButton>
      </DialogTitle>
      <form onSubmit={handleSubmit}>
        <DialogContent dividers>
          <DialogContentText>
          Будь ласка, заповніть свою інформацію в поля нижче:
          </DialogContentText>
          {isRegistr && (
            <TextField
            autoFocus
              margin="normal"
              variant="standard"
              id="name"
              label="Ім’я"
              type="text"
              fullWidth
              inputRef={nameRef}
              inputProps={{ minLength: 2 }}
              required
            />
            )}
             {isRegistr && (
            <TextField
            autoFocus
              margin="normal"
              variant="standard"
              id="secondname"
              label="Прізвище"
              type="text"
              fullWidth
              inputRef={secondNameRef}
              inputProps={{ minLength: 2 }}
              required
            />
          )}
          <TextField
            autoFocus={!isRegistr}
            margin="normal"
            variant="standard"
            id="email"
            label="Email"
            type="email"
            fullWidth
            inputRef={emailRef}
            required
          />
          <PasswordField {...{ passwordRef }} />
          {isRegistr && (
            <PasswordField
              passwordRef={confirmPasswordRef}
              id="confirmPassword"
              label="Confirm Password"
            />
          )}
        </DialogContent>
        <DialogActions sx={{ px: '19px'}}>
        <Button type="submit" variant="contained" endIcon={<Send />}>
            Submit
          </Button>
        </DialogActions>
      </form>
      <DialogActions sx={{ justifyContent: 'left', p: '5px 24px' }}>
        {isRegistr
          ? 'У вас є обліковий запис? Увійдіть зараз '
          : "У вас немає облікового запису? Створіть його зараз "}
        <Button onClick={() => setRegistr(!isRegistr)}>
          {isRegistr ? 'Вхід на сайт' : 'Реєстрація'}
        </Button>
      </DialogActions>
      <DialogActions sx={{ justifyContent: 'center', py: '24px' }}>
        <GoogleOneTapLogin />
      </DialogActions>
    </Dialog>
  );
};

export default Login;
